import { Row, Col, Container } from "react-bootstrap";
import { Hasil, ListCategori, NavbarComponent, Menus } from "./components";
import React, { Component } from "react";
import { API_URL } from "./utils/constanst";
import axios from "axios";
import swal from "sweetalert";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      menu: [],
      categoriYangDipilih: "Makanan",
    };
  }

  componentDidMount() {
    axios
      .get(API_URL + "products?category.nama=" + this.state.categoriYangDipilih)
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  changeCategory = (value) => {
    this.setState({
      categoriYangDipilih: value,
      menus: [],
      keranjangs: [],
    });
    axios
      .get(API_URL + "products?category.nama=" + value)
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });
  };
  masukKeranjang = (value) => {
    axios
      .get(API_URL + "pr?category.nama=" + value)
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });


    const keranjang = {
      jumlah: 1,
      total_harga: value.harga,
      product : value
    }

    axios
      .post(API_URL + "keranjangs", keranjang)
      .then((res) => {
        swal({
          title: "Sukses Masuk Keranjang",
          text: "Sukses memasukan "+ keranjang.product.nama + " Ke keranjang ",
          icon: "success",
          button : false,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { menus, categoriYangDipilih } = this.state;

    return (
      <div className="App">
        <NavbarComponent />
        <div className="mt-3">
          <Container fluid>
            <Row>
              <ListCategori
                changeCategory={this.changeCategory}
                categoriYangDipilih={categoriYangDipilih}
              />
              <Col>
                <h4>
                  <strong>Daftar Produk</strong>
                </h4>
                <hr />
                <Row>
                  {menus &&
                    menus.map((menu) => <Menus
                      key={menu.id}
                      menu={menu}
                      masukKeranjang = {this.masukKeranjang}
                    />)}
                </Row>
              </Col>
              <Hasil />
            </Row>
          </Container>
        </div>
      </div>
    );
  }
}
