import { Row,Col, Container } from 'react-bootstrap';
import { Hasil, ListCategori, NavbarComponent, Menus } from "./components"
import React, { Component } from 'react'
import { API_URL } from "./utils/constanst";
import axios from 'axios';


export default class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      menu: [],   
      categoriYangDipilih : 'Makanan'
    }
  }


  componentDidMount() {
    axios
      .get(API_URL + "products?category.nama=")
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  changeCategory = (value) => {
    this.setState({
      categoriYangDipilih: value,
      menus : []
    })
  }

  render() {
    const {menus} = this.state

    return (
       <div className="App">
          <NavbarComponent />
          <div className="mt-3">
            <Container fluid>
              <Row>
                <ListCategori />
                <Col>
                  <h4><strong>Daftar Produk</strong></h4>
                <hr />
                  <Row>
                  {menus && menus.map((menu) => (
                    <Menus
                      key = {menu.id}
                      menu = {menu}
                    />
                    ))} 
                  </Row>
                </Col>
                <Hasil/>
              </Row>
            </Container>
      </div>

    </div>
    )
  }
}
