          };
        }

      })
      .catch((error) => {
        console.log(error);
      });



  };

  render() {
    const { menus, categoriYangDipilih } = this.state;

    return (
      <div className="App">
        <NavbarComponent />
        <div className="mt-3">
          <Container fluid>
            <Row>
              <ListCategori
                changeCategory={this.changeCategory}
                categoriYangDipilih={categoriYangDipilih}
              />
              <Col>
                <h4>
                  <strong>Daftar Produk</strong>
                </h4>
                <hr />
                <Row>
                  {menus &&
                    menus.map((menu) => <Menus
                      key={menu.id}
                      menu={menu}
                      masukKeranjang = {this.masukKeranjang}
                    />)}
                </Row>
              </Col>
              <Hasil />
            </Row>
          </Container>
        </div>
      </div>
    );
  }
}
